// https://youtu.be/ID9YJXy3OJk
class Solution {
public:
    int shortestPath(vector<vector<int>>& grid, int k) {
        auto m{grid.size()};
        auto n{grid[0].size()};
        vector<vector<vector<int>>> memo(m, vector<vector<int>>(n, vector<int>(k+1, 0)));
        queue<tuple<int,int,int>> q; // x, y, kLeft
        q.emplace(0, 0, k);
        auto ans{0};
        auto count{1};
        while (!q.empty()) {
            while (count > 0) {
                auto [x, y, r] = q.front();
                q.pop();
                --count;
                if (x+1 == m && y+1 == n) return ans;
                if (memo[x][y][r] == 1) continue;
                memo[x][y][r] = 1;
                
                if (x+1 < m) {
                    if (grid[x+1][y] == 1) {
                        if (r > 0) q.emplace(x+1, y, r-1);
                    } else {
                        q.emplace(x+1, y, r);
                    }
                }
                
                if (y+1 < n) {
                    if (grid[x][y+1] == 1) {
                        if (r > 0) q.emplace(x, y+1, r-1);
                    } else {
                        q.emplace(x, y+1, r);
                    }
                }
                if (x-1 >= 0) {
                    if (grid[x-1][y] == 1) {
                        if (r > 0) q.emplace(x-1, y, r-1);
                    } else {
                        q.emplace(x-1, y, r);
                    }
                }
                
                if (y-1 >= 0) {
                    if (grid[x][y-1] == 1) {
                        if (r > 0) q.emplace(x, y-1, r-1);
                    } else {
                        q.emplace(x, y-1, r);
                    }
                }
                
            }
            count = q.size();
            ++ans;
        }
        return -1;
    }
};

